INSERT INTO `katalog` VALUES (1398,'JA.S - Janova společnost',2002,'',NULL,3,8,2,'Farnost Náměšť nad Oslavou','Lipová 212 Náměšť nad Oslavou','Náměšť nad Oslavou',10,3,NULL,'http://','lahvaaac@seznam.cz','JA.S - Janova společnost','Lipová 212','Náměšť nad Oslavou','67571','1','','Jaromír','Hlaváč','',1984,'','','','','',0,'','','','','',0,'','','','','',0,'','','','',NULL,0,'1','2','spirituál#Jednou budem dál#Mojžíš#Thuma mina\r\nargentinská lidová#Santo, snato\r\nzpěvy z Taizé#Laudate omnes genstes#Confitemini domino\r\nJ. A. Komenský#Soudce všeho světa\r\n','','','','','',7,0,0,0,2,3,2,5,3,4,19,13,58,25,'JA.S – vokálně instrumentální těleso působící převážně v chrámu sv. Jana Křtitele v Náměšti nad Oslavou. Soubor vznikl v roce 2002 a jsou zde zastoupeny skoro všechny skupiny lidí (děti, mládež, studenti, tatínci, pracující, důchodci…). Soubor tím naznačuje svoji otevřenost pro každého člověka dobré vůle…Zabývá se duchovní a liturgickou hudbou všech žánrů vhodnou pro doprovod mší svatých, svateb, křtů, pohřbů a různých dalších akcí…\r\nOd roku založení je vedoucím souboru Jaromír Hlaváč.\r\n','2008-07-11','2008-09-28 10:39:55','6',NULL,NULL);
